targets.rawSql:
SELECT cc.power AS avg_power , bce.co2_emissions AS avg_co2_emissions
FROM catalog_car cc
JOIN brand_co2_emissions bce ON cc.brand = bce.brand;

SELECT brand, COUNT(*) AS car_count, AVG(price) AS avg_price, MIN(price) AS min_price, MAX(price) AS max_price
FROM catalog_car
GROUP BY brand;

SELECT age, COUNT(*) AS count
FROM customer
GROUP BY age;

SELECT dependent_children, COUNT(*) AS count
FROM customer
GROUP BY dependent_children;

SELECT marital_status, AVG(income) AS avg_income
FROM customer
GROUP BY marital_status;

SELECT power, AVG(co2_emissions) AS avg_co2_emissions, AVG(price) AS avg_price
FROM catalog_car
GROUP BY power;

SELECT gender, COUNT(*) AS count
FROM customer_marketing
GROUP BY gender;
